        private Point Visualize(double coord)
        {
            return new Point((int)(1131 + 183 * Math.Cos((-coord + 65.5) * Math.PI / 180)), (int)(273 + 183 * Math.Sin((-coord + 65.5) * Math.PI / 180)));
        }
        private string ManWoman(int year)
        {
            if(year % 2 == 0) 
            { 
                return ", Мужское";
            }
            else
            {
                return ", Женское";
            }
        }
