        private void button2_Click(object sender, EventArgs e)
        {
            List<string> names = new List<string>();
            result = "";
            DBconnect db = DBconnect.getDataBase();

            string sqlExpression = $"SELECT * FROM Celebrities WHERE sun + 0.5 > {birthday.coords.sun} AND sun - 0.5 < {birthday.coords.sun}";
            SqliteCommand command = db.DoSql(sqlExpression);
            label108.Text += GetInfo(command, "Ярило, Мидгард (Солнце, Земля) - ", names);

            sqlExpression = $"SELECT * FROM Celebrities WHERE moon + 6.2 > {birthday.coords.moon} AND moon - 6.2 < {birthday.coords.moon}";
            command = db.DoSql(sqlExpression);
            label108.Text += GetInfo(command, "Месяц (Луна) - ", names);

            sqlExpression = $"SELECT * FROM Celebrities WHERE mercury_helio + 2.1 > {birthday.coords.mercury.helio} AND mercury_helio - 2.1 < {birthday.coords.mercury.helio}";
