        private string GetInfo(SqliteCommand command, string planet, List<string> names)
        {
            using (SqliteDataReader reader = command.ExecuteReader())
            {
                result = planet;
                if (reader.HasRows)
                {
                    while (reader.Read())
                    {
                        string name = (reader.GetValue(0)).ToString();
                        result += name + ", ";
                        names.Add(name);
                    }
                }
                else
                {
                    result = "";
                    return result;
                }
            }
            string result1 = result.Substring(0, result.Length - 2) + "\n";
            return result1;
        }

